(1, 'ACCEPT_HANDOVER', 'android.permission.ACCEPT_HANDOVER', NULL, 'Allows a calling app to continue a call which was started in another app. An example is a video calling app that wants to continue a voice call on the user\'s mobile network.', 'This permission protects access to the TelecomManager.acceptHandover(Uri, int, PhoneAccountHandle) which the receiving side of the handover uses to accept a handover.', 'dangerous', 28, 0, NULL, 0, NULL, NULL, 'Android', NULL, NULL, NULL, '2024-02-05 03:53:29'),
(2, 'ACCESS_ALL_EXTERNAL_STORAGE', 'android.permission.ACCESS_ALL_EXTERNAL_STORAGE', NULL, 'Allows the app to access external storage for all users.', NULL, 'signature', NULL, NULL, NULL, NULL, 15, NULL, NULL, NULL, NULL, NULL, '2024-02-05 03:53:29'),
(3, 'ACCESS_BACKGROUND_LOCATION', 'android.permission.ACCESS_BACKGROUND_LOCATION', NULL, 'Allows an app to access location in the background.', 'If you\'re requesting this permission, you must also request either ACCESS_COARSE_LOCATION or ACCESS_FINE_LOCATION. Requesting this permission by itself doesn\'t give you location access.', 'dangerous', 29, 0, NULL, 0, 10, NULL, 'Android', 'Access location in the background', 'If this is granted additionally to the approximate or precise location access the app can access the location while running in the background.', 'Dangerous|Instant', '2024-02-05 03:53:29'),
(4, 'ACCESS_BLOBS_ACROSS_USERS', 'android.permission.ACCESS_BLOBS_ACROSS_USERS', NULL, 'Allows an application to access data blobs across users. ', NULL, 'normal', 31, 0, NULL, 0, NULL, NULL, 'Android', NULL, NULL, NULL, '2024-02-05 03:53:29'),
(5, 'ACCESS_CHECKIN_PROPERTIES', 'android.permission.ACCESS_CHECKIN_PROPERTIES', NULL, 'Allows read/write access to the \"properties\" table in the checkin database, to change values that get uploaded.', NULL, 'normal', 1, 0, NULL, 1, NULL, NULL, 'Android', NULL, NULL, NULL, '2024-02-05 03:53:29'),
(6, 'ACCESS_COARSE_LOCATION', 'android.permission.ACCESS_COARSE_LOCATION', 'ACCESS_FINE_LOCATION', 'Allows an app to access approximate location.', 'Allows the app to get your approximate location. This location is derived by location services using network location sources such as cell towers and Wi-Fi. These location services must be turned on and available to your device for the app to use them. Apps may use this to determine approximately where you are.', 'dangerous', 1, 0, NULL, 0, 10, NULL, 'Android', 'Access approximate location (network-based)', 'Allows the app to get your approximate location. This location is derived by location services using network location sources such as cell towers and Wi-Fi. These location services must be turned on and available to your device for the app to use them. Apps may use this to determine approximately where you are.', 'Dangerous', '2024-02-05 03:53:29'),
