(85, 'CHANGE_COMPONENT_ENABLED_STATE', 'android.permission.CHANGE_COMPONENT_ENABLED_STATE', NULL, 'Allows an application to change whether an application component (other than its own) is enabled or not.', NULL, 'normal', 1, 0, NULL, 1, NULL, NULL, 'Android', 'Enable or disable application components', 'Allows an application to change whether or not a component of another application is enabled. Malicious applications can use this to disable important phone capabilities. It is important to be careful with permission, as it is possible to bring application components into an unusable, inconsistent or unstable state.', 'Signature|System', '2024-02-05 03:53:29'),
(86, 'CHANGE_CONFIGURATION', 'android.permission.CHANGE_CONFIGURATION', NULL, 'Allows an application to modify the current configuration, such as locale. ', NULL, 'signature|privileged|development', 1, 0, NULL, 0, NULL, NULL, 'Android', 'Change system display settings', 'Allows the app to change the current configuration, such as the locale or overall font size.', 'Signature|System|Development', '2024-02-05 03:53:29'),
(87, 'CHANGE_NETWORK_STATE', 'android.permission.CHANGE_NETWORK_STATE', NULL, 'Allows applications to change network connectivity state. ', NULL, 'normal', 1, 0, NULL, 0, 11, NULL, 'Android', 'Change network connectivity', 'Allows the app to change the state of network connectivity.', 'Normal', '2024-02-05 03:53:29'),
(88, 'CHANGE_WIFI_MULTICAST_STATE', 'android.permission.CHANGE_WIFI_MULTICAST_STATE', NULL, 'Allows applications to enter Wi-Fi Multicast mode. ', NULL, 'normal', 4, 0, NULL, 0, 11, NULL, 'Android', 'Allow wi-fi multicast reception', 'Allows the app to receive packets sent to all devices on a Wi-Fi network using multicast addresses, not just your phone. It uses more power than the non-multicast mode.', 'Normal', '2024-02-05 03:53:29'),
(89, 'CHANGE_WIFI_STATE', 'android.permission.CHANGE_WIFI_STATE', NULL, 'Allows applications to change Wi-Fi connectivity state. ', NULL, 'normal', 1, 0, NULL, 0, 11, NULL, 'Android', 'Connect and disconnect from wi-fi', 'Allows the app to connect to and disconnect from Wi-Fi access points and to make changes to device configuration for Wi-Fi networks.', 'Normal', '2024-02-05 03:53:29'),
