(132, 'GET_PACKAGE_SIZE', 'android.permission.GET_PACKAGE_SIZE', NULL, 'Allows an application to find out the space used by any package. ', NULL, 'normal', 1, 0, NULL, 0, 16, NULL, 'Android', 'Measure app storage space', 'Allows the app to retrieve its code, data, and cache sizes', 'Normal', '2024-02-05 03:53:29'),
(133, 'GET_TASKS', 'android.permission.GET_TASKS', NULL, 'This constant was deprecated in API level 21. No longer enforced. ', '', 'normal', 1, 21, NULL, 0, 16, NULL, 'Android', 'Retrieve running apps', 'Allows the app to retrieve information about currently and recently running tasks. This may allow the app to discover information about which applications are used on the device.', 'Normal', '2024-02-05 03:53:29'),
(134, 'GLOBAL_SEARCH', 'android.permission.GLOBAL_SEARCH', NULL, 'This permission can be used on content providers to allow the global search system to access their data.', 'Typically it used when the provider has some permissions protecting it (which global search would not be expected to hold), and added as a read-only permission to the path in the provider where global search queries are performed. This permission can not be held by regular applications; it is used by applications to protect themselves from everyone else besides global search.', 'signature|privileged', 4, 0, NULL, 0, NULL, NULL, 'Android', NULL, NULL, NULL, '2024-02-05 03:53:29'),
