(139, 'INJECT_EVENTS', 'android.permission.INJECT_EVENTS', NULL, 'Allows an application to inject user events (keys, touch, trackball) into the event stream and deliver them to ANY window. Without this permission, you can only deliver events to windows in your own process. Very few applications should need to use this permission.', NULL, 'signature', 1, NULL, NULL, NULL, NULL, NULL, NULL, 'Press keys and control buttons', 'Allows the app to deliver its own input events (key presses, etc.) to other apps. Malicious apps may use this to take over the phone.', 'Signature', '2024-02-05 03:53:29'),
(140, 'INSTALL_LOCATION_PROVIDER', 'android.permission.INSTALL_LOCATION_PROVIDER', NULL, 'Allows an application to install a location provider into the Location Manager.', NULL, 'normal', 4, 0, NULL, 1, 10, NULL, 'Android', NULL, NULL, NULL, '2024-02-05 03:53:29'),
(141, 'INSTALL_PACKAGES', 'android.permission.INSTALL_PACKAGES', NULL, 'Allows an application to install packages.', NULL, 'normal', 1, 0, NULL, 1, 2, NULL, 'Android', 'Directly install applications', 'Allows an application to install new or updated Android packages. Malicious applications can use this to add new applications with arbitrarily powerful permissions.', 'Signature|System', '2024-02-05 03:53:29'),
(142, 'INSTALL_SHORTCUT', 'com.android.launcher.permission.INSTALL_SHORTCUT', NULL, 'Allows an application to install a shortcut in Launcher.', 'In Android O (API level 26) and higher, the INSTALL_SHORTCUT broadcast no longer has any effect on your app because it\'s a private, implicit broadcast. Instead, you should create an app shortcut by using the requestPinShortcut() method from the ShortcutManager class.  Constant Value: \"com.android.launcher.permission.INSTALL_SHORTCUT\"', 'normal', 19, 0, NULL, 0, 16, NULL, 'Android', 'Install shortcuts', 'Allows an application to add Homescreen shortcuts without user intervention.', 'Normal', '2024-02-05 03:53:29'),
