(148, 'INTERNET', 'android.permission.INTERNET', NULL, 'Allows applications to open network sockets. ', NULL, 'normal', 1, 0, NULL, 0, 11, NULL, 'Android', 'Have full network access', 'Allows the app to create network sockets and use custom network protocols. The browser and other applications provide means to send data to the internet, so this permission is not required to send data to the internet.', 'Normal', '2024-02-05 03:53:29'),
(149, 'KILL_BACKGROUND_PROCESSES', 'android.permission.KILL_BACKGROUND_PROCESSES', NULL, 'Allows an application to call ActivityManager.killBackgroundProcesses(String).', 'As of Android version Build.VERSION_CODES.UPSIDE_DOWN_CAKE, the ActivityManager.killBackgroundProcesses(String) is no longer available to third party applications. For backwards compatibility, the background processes of the caller\'s own package will still be killed when calling this API. If the caller has the system permission KILL_ALL_BACKGROUND_PROCESSES, other processes will be killed too.', 'normal', 8, 0, NULL, 0, 6, NULL, 'Android', 'Kill background processes', 'Allows an application to kill background processes of other applications, even if memory is not low.', 'Normal', '2024-02-05 03:53:29'),
(150, 'LAUNCH_CAPTURE_CONTENT_ACTIVITY_FOR_NOTE', 'android.permission.LAUNCH_CAPTURE_CONTENT_ACTIVITY_FOR_NOTE', NULL, 'Allows an application to capture screen content to perform a screenshot using the intent action Intent.ACTION_LAUNCH_CAPTURE_CONTENT_ACTIVITY_FOR_NOTE.', 'Intended for use by ROLE_NOTES only.', 'internal|role', 34, 0, NULL, 0, NULL, NULL, 'Android', NULL, NULL, NULL, '2024-02-05 03:53:29'),
(151, 'LAUNCH_MULTI_PANE_SETTINGS_DEEP_LINK', 'android.permission.LAUNCH_MULTI_PANE_SETTINGS_DEEP_LINK', NULL, 'An application needs this permission for Settings.ACTION_SETTINGS_EMBED_DEEP_LINK_ACTIVITY to show its Activity embedded in Settings app. ', NULL, 'normal', 32, 0, NULL, 0, NULL, NULL, 'Android', NULL, NULL, NULL, '2024-02-05 03:53:29'),
(152, 'LOADER_USAGE_STATS', 'android.permission.LOADER_USAGE_STATS', NULL, 'Allows a data loader to read a package\'s access logs. The access logs contain the set of pages referenced over time. Declaring the permission implies intention to use the API and the user of the device can grant permission through the Settings application.  A data loader has to be the one which provides data to install an app. A data loader has to have both permission:LOADER_USAGE_STATS AND appop:LOADER_USAGE_STATS allowed to be able to access the read logs.', NULL, 'signature|privileged|appop', 30, 0, NULL, 0, NULL, NULL, 'Android', NULL, NULL, NULL, '2024-02-05 03:53:29'),
