INSERT INTO `android_permission_categories` (`category_id`, `category_name`, `description`, `risk_level`, `common_usage`, `potential_misuse`, `last_updated`) VALUES
(1, 'System & Device Control', 'Managing system-level functions, device settings, and resource access.', 'High', 'Required for system maintenance, settings adjustment, and accessing critical device functions.', 'Potential misuse includes changing system settings without user consent or locking out user access.', '2024-03-11 17:15:15'),
(2, 'Package & Application Management', 'Installation, update, and removal of apps, including package information management.', 'Medium', 'Essential for app stores, app managers, and system tools that manage application lifecycles.', 'Malicious apps may use these permissions to uninstall other apps or install unwanted software.', '2024-03-11 17:15:24'),
(3, 'Phone\r\n', 'Phone call handling, call log access, and managing phone state.', 'Medium', 'Necessary for dialer apps, call blockers, and apps that enhance calling features.', 'May be misused to intercept or record calls without user knowledge.', '2024-03-11 17:15:31'),
(4, 'SMS/MMS', 'Permissions for messaging services, including SMS/MMS and other communication forms.', 'High', 'Used by messaging apps to send, receive, and manage communications.', 'Malicious apps might exploit these permissions to send messages without consent or eavesdrop on communications.', '2024-01-31 11:15:52'),
(5, 'Voicemail', 'Managing voicemail services, including access and modification.', 'Medium', 'Allows voicemail apps to access and manage voicemail messages.', 'Misuse includes accessing or modifying voicemail without user permission.', '2024-03-11 17:17:11'),
(6, 'Processes & Services', 'Operations in the background, including data access and start-up processes.', 'Medium', 'Essential for apps that require running background tasks for updates, notifications, or services.', 'Can be misused by malware to run background tasks without user knowledge, draining battery or consuming data.', '2024-03-11 17:17:22'),
(7, 'Cross-User Data Access', 'Allowing access to or manipulation of data across different user profiles.', 'High', 'Useful for apps designed to work in multi-user environments, facilitating data sharing and access between profiles.', 'Potential misuse includes unauthorized access to or sharing of personal data across profiles without consent.', '2024-03-11 17:17:31'),
(8, 'Multi-Profile Management', 'Permissions for managing and affecting multiple user profiles on a device.', 'High', 'Necessary for system or profile management apps to configure settings or permissions across profiles.', 'Misuse could lead to unauthorized changes to user profiles or access restrictions.', '2024-01-31 07:16:17'),
(9, 'Diagnostic', 'Permissions for accessing diagnostic tools and performing diagnostic operations.', 'Medium', 'Used by system tools and utilities to diagnose device issues and optimize performance.', 'Malicious apps may misuse diagnostic permissions to gather sensitive device information or manipulate device performance.', '2024-01-31 07:16:17'),
(10, 'Location', 'Permissions related to accessing coarse and fine location data, and other location-specific features.', 'Critical', 'Crucial for navigation, weather, and location-based services to provide contextual information.', 'Misuse can lead to privacy violations through unauthorized tracking or location spoofing.', '2024-01-31 07:20:29'),
(11, 'Network', 'Internet access, network status, and connectivity management.', 'Medium', 'Foundational for any app that requires internet connectivity to function, from browsers to social media.', 'Exploited by malware to transmit data to remote servers or download additional malicious payloads.', '2024-03-11 17:18:01'),
(12, 'Account Management', 'Managing user accounts and handling authentication.', 'High', 'Important for apps requiring user login and authentication to access personalized content.', 'Potential misuse includes phishing attempts and unauthorized access to user accounts.', '2024-03-11 17:17:50'),
(13, 'Environmental Sensing', 'Permissions for accessing environmental sensors.', 'Low', 'Used by weather apps to provide localized conditions.', 'Could be misused to infer user behavior or patterns without explicit consent.', '2024-01-31 11:13:38'),
(14, 'File Access', 'Internal file access and management.', 'High', 'Necessary for file managers, media players, and apps requiring access to device storage.', 'Risk of unauthorized access to or manipulation of personal files and media.', '2024-03-11 17:17:41'),
(15, 'External Storage', 'Accessing and modifying external storage.', 'High', 'Essential for apps that store or retrieve data from external SD cards or USB storage.', 'Malicious apps may exploit this to read or write data on external storage without user consent.', '2024-03-11 17:17:05'),
(16, 'Application Information', 'Permissions for accessing information about installed applications.', 'Medium', 'Utilized by app managers, security tools, and system optimizers to gather information on installed apps.', 'Can be exploited to gather sensitive information or to target specific apps for vulnerabilities.', '2024-01-31 07:16:17'),
(17, 'Task Management', 'Task scheduling, process control, and system task interactions.', 'Medium', 'Important for apps that manage background tasks, automate processes, or control app execution.', 'Misused by malicious apps to disrupt normal app operations or to keep malware running.', '2024-03-11 17:16:58'),
(18, 'Security & Privacy Settings', 'Security settings, privacy considerations, and permission management.', 'High', 'Crucial for security apps and settings managers to enforce or modify security policies.', 'Potential for misuse in bypassing security measures or altering privacy settings without user knowledge.', '2024-03-11 17:16:51'),
(19, 'User Interface', 'Controlling or accessing UI features.', 'Low', 'Necessary for customization apps, launchers, and tools enhancing user interface.', 'May be misused to overlay UI elements for phishing attacks or to capture user input.', '2024-03-11 17:16:41'),
(20, 'Accessibility', 'Accessibility services.', 'Medium', 'Essential for apps designed to improve device accessibility for users with disabilities.', 'Risk of misuse in monitoring user actions or retrieving sensitive information through accessibility events.', '2024-03-11 17:16:30'),
(21, 'Hardware', 'Controlling specific hardware features like cameras.', 'Medium', 'Used by camera apps, flashlight tools, and apps requiring hardware control.', 'Misuse includes unauthorized access to hardware features, potentially invading user privacy.', '2024-03-11 17:16:12'),
(23, 'Video', 'Permissions related to accessing and managing video recording and playback.', 'Medium', 'Necessary for apps that need to record video, play video, or manage video settings.', 'May be misused to record video without user consent or in contexts that compromise user privacy.', '2024-03-11 18:19:50'),
(24, 'Audio', 'Permissions related to accessing and managing audio playback and recording.', 'Medium', 'Necessary for apps that need to record audio, play audio, or manage audio settings.', 'May be misused to record audio without user consent or in contexts that violate user privacy.', '2024-03-11 18:14:29'),
(25, 'Contacts', 'Permissions related to accessing and managing contacts on the device.', 'Medium', 'Necessary for apps that need to read, create, or manage contacts.', 'May be misused to harvest contact information without user consent.', '2024-03-11 18:07:06'),
(26, 'Data & Privacy Protection', 'Data security and user privacy protection.', 'High', 'Important for privacy-focused apps and services that secure user data.', 'Potential misuse includes deceptive practices that falsely claim to enhance privacy while collecting user data.', '2024-03-11 17:15:43'),
(27, 'Cloud Services', 'Permissions for accessing cloud services and data synchronization.', 'Medium', 'Essential for apps that store data in the cloud or synchronize data across devices.', 'Misuse might involve unauthorized access to or leakage of cloud-stored personal data.', '2024-01-31 11:14:45'),
(28, 'Notifications', 'Permissions for controlling app notifications and alert settings.', 'Low', 'Used by notification managers and apps that customize alert behaviors.', 'Could be misused to suppress important notifications or to deliver unwanted advertisements.', '2024-01-31 11:15:25'),
(29, 'Custom Features', 'Permissions for accessing and controlling device-specific features or manufacturer-added functionalities.', 'Medium', 'Relevant for apps leveraging unique device capabilities or additional hardware.', 'Risks include exploiting proprietary features for unauthorized purposes or accessing restricted data.', '2024-01-31 11:14:59'),
(30, 'Bluetooth', 'Permissions related to Bluetooth functionality and communication.', 'Medium', 'Used by apps for Bluetooth pairing, device communication, and data transfer.', 'Potential misuse includes unauthorized access or data interception via Bluetooth connections.', '2024-01-31 08:25:29'),
(31, 'Biometrics', 'Permissions related to biometric authentication methods such as fingerprint or facial recognition.', 'High', 'Used by security and authentication apps to implement biometric authentication.', 'Potential misuse includes unauthorized access to biometric data or biometric authentication bypass.', '2024-01-31 08:25:32');
